require('request')(
  {
    url: 'http://127.0.0.1:8888/prizoners.png',
    encoding: null,
    withCredentials: false
  }
  , function (e,r,b) {
    var type    = r.headers["content-type"];
    var prefix  = "data:" + type + ";base64,";
    var base64  = new Buffer(b, 'binary').toString('base64');
    var dataURI = prefix + base64;

    // in Node :    data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAEAAAABACAYAAACqaXHe...
    // in Browser : data:image/png;base64,/VBORw0KGgoAAAANSUhEUgAAAEAAAABACAYAAAD9aXH9...
    console.log(dataURI);
  }
);
